mapper.insert:
  	insert into orders(	  ord_num
  						, ord_code
						, user_id
						, act_num
						, ord_quantity
						, ord_pay_price
						, pay_method
						, ord_date
						, ord_refund 
						, ord_status) 

			  	values(	  #{ordNum}
			  			, #{ordCode}
			  			, #{userId}
			  			, #{actNum}
			  			, #{ordQuantity}
			  			, #{ordPayPrice}
			  			, #{payMethod}
			  			, #{ordDate}
			  			, #{ordRefund} 
			  			, #{ordStatus});

insert into point(
	point_num
   ,user_id
   ,point_date
   ,point_type
   ,point_balance
   ,point_used
	)
values(
	 (select a.pointnum
	    from (select max(point_num) + 1 as pointnum
	    	    from point) a)
	,#{userId}
	,#{pointDate}
	,"포인트 사용"
	,#{pointBalance}
	,#{pointUsed}
	)
